      fTemp = fTemp - 273.15;
      sprintf((char *)ucaBuf, "温度：%.2f\n\r", fTemp);
      MULTIEDIT_AddText(hItem, (char *)ucaBuf);

      usVal = batVoltage();
      fTemp = (float)usVal / 1000;
      sprintf((char *)ucaBuf, "电压：%.2f", fTemp);
      strcat((char *)ucaBuf, "V\n\r");
      MULTIEDIT_AddText(hItem, (char *)ucaBuf);

      sCurrent = batCurrent();
      fTemp = (float)sCurrent / 1000;
      sprintf((char *)ucaBuf, "电流：%.2f", fTemp);
      strcat((char *)ucaBuf, "A\n\r");
      MULTIEDIT_AddText(hItem, (char *)ucaBuf);

