      sprintf((char *)ucaBuf, "相对剩余：%d", usVal);
      strcat((char *)ucaBuf, "%\n\r");
      MULTIEDIT_AddText(hItem, (char *)ucaBuf);

      usVal = batAbsoluteStateOfCharge();
      sprintf((char *)ucaBuf, "绝对剩余：%d", usVal);
      strcat((char *)ucaBuf, "%\n\r");
